import { __decorate } from "tslib";
import { Component, ViewChild } from "@angular/core";
import { DayPilot } from "@daypilot/daypilot-lite-angular";
let CalendarComponent = class CalendarComponent {
    constructor(ds, interventoService) {
        this.ds = ds;
        this.interventoService = interventoService;
        this.events = [];
        this.date = DayPilot.Date.today();
        this.configNavigator = {
            showMonths: 3,
            cellWidth: 25,
            cellHeight: 25,
            onVisibleRangeChanged: args => {
                this.loadEvents();
            }
        };
        this.configDay = {};
        this.configWeek = {
            viewType: "Week",
            onTimeRangeSelected: async (args) => {
                const modal = await DayPilot.Modal.prompt("Crea un appuntamento:", "Breve descrizione del tipo di intervento");
                const dp = args.control;
                dp.clearSelection();
                //test save date
                // this.authService.login(username, password).subscribe(
                console.log(typeof modal.result);
                console.log(typeof args.start);
                console.log(typeof args.end);
                console.log(typeof DayPilot.guid());
                if (!modal.result) {
                    return;
                }
                dp.events.add(new DayPilot.Event({
                    start: args.start,
                    end: args.end,
                    id: DayPilot.guid(),
                    text: modal.result
                }));
            }
        };
        this.configMonth = {};
        this.viewWeek();
        //this.configWeek.headerDateFormat="dddd MMMM d, yyyy";
        this.configWeek.eventMoveHandling = "Disabled";
        //  this.configWeek.eve
    }
    selectTomorrow() {
        this.date = DayPilot.Date.today().addDays(1);
    }
    changeDate(date) {
        this.configDay.startDate = date;
        this.configWeek.startDate = date;
        this.configMonth.startDate = date;
    }
    ngAfterViewInit() {
        this.loadEvents();
    }
    loadEvents() {
        const from = this.nav.control.visibleStart();
        const to = this.nav.control.visibleEnd();
        this.ds.getEvents(from, to).subscribe(result => {
            this.events = result;
        });
    }
    viewDay() {
        this.configNavigator.selectMode = "Day";
        this.configDay.visible = true;
        this.configWeek.visible = false;
        this.configMonth.visible = false;
    }
    viewWeek() {
        this.configNavigator.selectMode = "Week";
        this.configDay.visible = false;
        this.configWeek.visible = true;
        this.configMonth.visible = false;
    }
    viewMonth() {
        this.configNavigator.selectMode = "Month";
        this.configDay.visible = false;
        this.configWeek.visible = false;
        this.configMonth.visible = true;
    }
};
